import React, { useState } from "react";
import { useEffect } from "react";
import CartService from "../services/CartService";
import OrderService from "../services/OrderService";
import ProductService from "../services/ProductService";
import TableRow from "../uicomponent/TableRow";

const Cart = ({ userId }) => {
  const [cartList, setcartList] = useState([]);
  const [loading, setLoading] = useState(false);

  const [orderList, setOrderList] = useState([]);

  useEffect(() => {
    const fetchList = (userId) => {
      setLoading(true);
      CartService.getCartByUserId(userId)
        .then((response) => {
          setcartList(response.data);
          const orderArray = [];
          for (let i = 0; i < cartList.length; i++) {
            const order = {
              userId: cartList[i].userId,
              productId: cartList[i].productId,
              quantity: cartList[i].quantity,
            };
            orderList.push(order);
          }
          setOrderList(orderArray);
        })
        .catch((error) => {
          console.log(error);
        });
      setLoading(false);
    };
    fetchList(userId);

    console.log("Shubham", orderList);
  }, []);

  const placeOrder = () => {
    OrderService.addOrder(orderList)
      .then((response) => {
        console.log(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
      console.log("Shubham",orderList);
  };
  return (
    <div className="table">
      <table id="productCartBody">
        <thead>
          <tr>
            <th className="firstItem">Product Name</th>
            <th>Price</th>
            <th>Quantity</th>
          </tr>
        </thead>
        <tbody>
          {!loading && (
            <>
              {cartList.map((cart) => (
                <TableRow cart={cart} key={cart.id} />
              ))}
            </>
          )}
        </tbody>
      </table>
      <div className="orderBtnDiv">
        <button
          onClick={() => placeOrder()}
          className="orderBtn"
          id="placeOrderButton"
        >
          Place Order
        </button>
      </div>
    </div>
  );
};

export default Cart;
