import React, { useState } from 'react'
import { useEffect } from 'react';
import CartService from '../services/CartService';
import ProductService from '../services/ProductService';

const Cart = ({userId}) => {

const [cartList, setcartList] = useState([]);
const [loading,setLoading] = useState(false);

useEffect(() => {
    const fetchList = (userId) =>{
        setLoading(true);
        CartService.getCartByUserId(userId)
        .then((response) =>{
            console.log(response);
            setcartList(response.data);
        }).catch((error) =>{
            console.log(error);
        })
        setLoading(false);
    }
    fetchList(userId);
    console.log(cartList);
}, [])
const [product, setProduct] = useState({});
const getProd =async (id) =>{
    try{
        const response = await ProductService.getProductById(id); 
        setProduct(response.data);
        console.log(response.data);
    }catch(error)
    {
        console.log(error);
    }
    
}
  return (
    <div className='table'>
        <table id='productCartBody'>
            <thead>
                <tr >
                <th className='firstItem'>Product Name</th>
                <th>Price</th>
                <th>Quantity</th>
                </tr>
            </thead>
            <tbody>
                {!loading && (
                    <tr>
                        { 
                          cartList.map((cart) =>(
                            getProd(cart.productId)
                          ))
                        }
                    </tr>
                )}
                <tr id='product1'>
                    <td className='firstItem'><span className='indexNo'>1. </span>Carrom Board</td>
                    <td>$120</td>
                    <td>3 ps</td>
                </tr>
                
            </tbody>

        </table>
        <div className='orderBtnDiv'>
            <button className='orderBtn' id='placeOrderButton'>Place Order</button>
        </div>
    </div>
  )
}

export default Cart
