import React, { useState } from 'react'
import { useEffect } from 'react';
import CartService from '../services/CartService';
import ProductService from '../services/ProductService';

const TableRow = ({cart, deleteCart}) => {
  const[product, setProduct] = useState({
    
  });
  useEffect(() => {
    const fetchProduct = async (productId) =>{
        try{
            const response = await ProductService.getProductById(productId);
            setProduct(response.data);
            console.log("Product",product);
        }catch(error)
        {
            console.log(error);
        }
    }
    let id = cart.productId;
   fetchProduct(id)
  }, [])
  

  return (
      <tr>
        <td>{cart.id}.{product.name}</td>
        <td>{product.price * cart.quantity}</td>
        <td><select value={cart.quantity}>{[...Array(product.quantity).keys()].map((x) =>(
          <option key={x+1} value={x+1} selected={cart.id = option.value}>{x+1}</option>
        ))}</select></td>
        <td><button onClick={(e) =>{deleteCart(e,cart.id)}}>Delete</button></td>
      </tr>

  )
}

export default TableRow
