import React, { useState } from "react";
import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import CartService from "../services/CartService";
import OrderService from "../services/OrderService";
import ProductService from "../services/ProductService";
import SimpleLoginSession from "../SimpleLoginSession";
import TableRow from "../uicomponent/TableRow";

const Cart = ({ userId }) => {
  const [cartList, setcartList] = useState([]);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  let user_id = userId;
  useEffect(() => {
    if(!SimpleLoginSession.isSignIn())
    {
      navigate('/signIn');
    }else{
      user_id = SimpleLoginSession.getUser().id;
    }
    const fetchList = (user_id) => {
      setLoading(true);
      CartService.getCartByUserId(user_id)
        .then((response) => {
          setcartList(response.data);
          
        })
        .catch((error) => {
          console.log(error);
        });
      setLoading(false);
    };
    fetchList(user_id);
  }, []);

  const placeOrder = (e) => {
    e.preventDefault();
    const orderArray = [];
          for (let i = 0; i < cartList.length; i++) {
            const order = {
                userId: cartList[i].userId,
                productId: cartList[i].productId,
                quantity: cartList[i].quantity,
            };
            orderArray.push(order);
          }
    OrderService.addOrder(orderArray)
      .then((response) => {
         console.log(response.data);
         for(let i=0; i<cartList.length; i++)
         {
          deleteCart(e,cartList[i].id);
          console.log(cartList[i]);
         }
         
      })
      .catch((error) => {
        console.log(error);
      });
      console.log("Shubham",orderArray);
  };

  const deleteCart = (e,cartId) =>{
    e.preventDefault();
    CartService.deleteCart(cartId)
    .then((response) =>{
      if(cartList){
        setcartList((preventElement) =>{
          return preventElement.filter((cart) => cart.id != cartId);
        })
      }
    }).catch((error) =>{
      console.log(error);
    })
  }
  return (
    <div className="table">
      <table id="productCartBody">
        <thead>
          <tr>
            <th className="firstItem">Product Name</th>
            <th>Price</th>
            <th>Quantity</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {!loading && (
            <>
              {cartList.map((cart) => (
                <TableRow cart={cart} key={cart.id}  deleteCart={deleteCart}/>
              ))}
            </>
          )}
        </tbody>
      </table>
      <div className="orderBtnDiv">
        <button
          onClick={(e) => placeOrder(e)}
          className="orderBtn"
          id="placeOrderButton"
        >
          Place Order
        </button>
      </div>
    </div>
  );
};

export default Cart;
